templates.templateContent:
{#
!! 20210615 JOGRIM
#}

{# PROVISION OPENROAMING ANQP PROFILE #}
{% if anqp_provision == 0 %}
  {# Clear Previous Configuration #}
  no wireless hotspot anqp-server OpenRoaming type open-roaming
{% elif anqp_provision == 1 %}
  {# Clear Previous Configuration #}
  no wireless hotspot anqp-server OpenRoaming type open-roaming
  !
  {# Provision New OpenRoaming ANQP #}
  wireless hotspot anqp-server OpenRoaming type open-roaming
   anqp-domain-id 0
   domain cisco.com
   hessid 0000.0000.0000
   nai-realm 1.openroaming.org
    eap-method eap-ttls
     inner-auth-non-eap pap
   nai-realm 2.openroaming.org
    eap-method eap-ttls
     inner-auth-non-eap mschap-v2
   nai-realm 3.openroaming.org
    eap-method eap-tls
   nai-realm 4.openroaming.org
    eap-method eap-aka
   nai-realm open.openroaming.org
    eap-method eap-tls
    eap-method eap-ttls
     inner-auth-non-eap mschap-v2
     inner-auth-non-eap pap
    eap-method eap-aka
   network-type free-public internet-access allowed
   wan-metrics downlink-load 0
   wan-metrics downlink-speed 0
   wan-metrics load-measurement-duration 0
   wan-metrics uplink-load 0
   wan-metrics uplink-speed 0
   !
   {# Operator ID can be customized. #}
   operator OpenRoaming eng
   ! 
   {# Advertise Selected ANQP Organizational IDs #}
   {% for orgid in orgids %}
     open-roaming-oi {{ orgid }} beacon
   {% endfor %}
{% endif %}

{# Associate ANQP Profile to WLAN Profiles that contain "SECURE" #}
{% for profile in __policyprofile %}
  {% if "SECURE" in profile %}
  wireless profile policy {{ profile }}
    shutdown
    hotspot anqp-server OpenRoaming
    no shutdown
  {% endif %}
{% endfor %}

