templates.templateContent:
{#
!! 20210615 JOGRIM
#}

{# Enables foreign AP 802.11 interference avoidance in the channel assignment #}
{# Normally enabled by default, but re-confirming. #}
{% if rrmForeign == 0 %}
  no ap dot11 5ghz rrm channel foreign
  no ap dot11 24ghz rrm channel foreign
{% elif rrmForeign == 1 %}
  ap dot11 5ghz rrm channel foreign
  ap dot11 24ghz rrm channel foreign
{% endif %}

{# Enables non-802.11 noise avoidance in the channel assignment #}
{# Normally enabled by default, but re-confirming. #}
ap dot11 5ghz rrm channel noise
ap dot11 24ghz rrm channel noise

{# Configure persistent non-WiFi device avoidance in the 802.11 channel assignment #}
{% if rrmPersistent == 0 %}
  no ap dot11 5ghz rrm channel device
  no ap dot11 24ghz rrm channel device
{% elif rrmPersistent == 1 %}
  ap dot11 5ghz rrm channel device
  ap dot11 24ghz rrm channel device
{% endif %}

{# Monitor Country Channel List #}
{# To monitor all channels, change to "all" #}
ap dot11 24ghz rrm monitor channel-list country
ap dot11 5ghz rrm monitor channel-list country

{# Globally Enable CleanAir #}
{# ap dot11 5ghz cleanair #}
{# ap dot11 24ghz cleanair #}

{# Enable EDRRM for 5 GHz #}
ap dot11 5ghz rrm channel cleanair-event

{# Enable EDRRM Rogue Contribution for 5 GHz #}
ap dot11 5ghz rrm channel cleanair-event rogue-contribution

{# Configure FRA Interval #}
ap fra interval 24

{# Configure FRA #}
{% if fra == 0 %}
  no ap fra
{% elif fra == 1 %}
  ap fra
{% endif %}

{# Configure DCA interval value #}
{% if dcaInterval5 is number %}
  ap dot11 5ghz rrm channel dca interval {{ dcaInterval5 }}
{% endif %}

{% if dcaInterval24 is number %}
  ap dot11 24ghz rrm channel dca interval {{ dcaInterval24 }}
{% endif %}

{# Enables Tx Power Control to be channel aware #}
ap dot11 5ghz rrm tpc tpcv1-chan-aware

{# Enable Tri-Radio in C9130 #}
{% if triradio == 0 %}
  no ap tri-radio
{% elif triradio == 1 %}
  ap tri-radio
{% endif %}

{# Restart RRM -- Startup mode will run for 100 minutes, #}
{# reaching a solution generally within 30 to 40 minutes. #}
{# !! WARNING !! -- This is disruptive to clients. #}
{% if restartrrm == 1 %}
  #MODE_ENABLE
  ap dot11 5ghz rrm dca restart
  ap dot11 24ghz rrm dca restart
  #MODE_END_ENABLE
{% endif %}

