templates.templateContent:
{#
!! 20210615 JOGRIM
#}

ap profile default-ap-profile

  {# Configure SYSLOG host on AP #}
  {% if apSyslog != "" %}
    syslog host {{ apSyslog }}
  {% else %}
    ## DEFAULT
    syslog host 172.18.1.10
  {% endif %}

  {# Configure admin credentials on AP. Enables AP serial console access. -#}
  {# Only enable for lab use or troubleshooting. #}
  {% if apCredentials == 0 %}
    no ssh
    no telnet
    no mgmtuser
  {% elif apCredentials == 1 %}
    mgmtuser username nettech password 0 C!sco#123 secret 0 C!sco#123
  {% endif %}

  {# NTP is required for Hyperlocation/FastLocate -#}
  {% if apNtp != "" %}
    ntp ip {{ apNtp }}
  {% else %}
    ## DEFAULT
    ntp ip 172.18.1.10
  {% endif %}

  {# Configures Hyperlocation/BLE/FastLocate features on all supported APs -#}
  {# FastLocate performance impact if not 4800/9120/9130 -#}
  {% if apFastlocate == 0 %}
    no hyperlocation
  {% elif apFastlocate == 1 %}
    hyperlocation
  {% endif %}

  {# AP trusts upstream DSCP instead of User Priority -#}
  {# Required for QOS to work properly -#}
  qos-map trust-dscp-upstream

